mapper.select:
SELECT c.*, u.username as userName, u.avatar as userAvatar, s.name as scenicName FROM `comment` c LEFT JOIN `user` u ON c.user_id = u.id LEFT JOIN `scenic_spot` s ON c.scenic_id = s.id
AND c.content LIKE CONCAT('%', #{content}, '%')
AND c.scenic_id = #{scenicId}
AND c.user_id = #{userId}
AND s.name LIKE CONCAT('%', #{scenicName}, '%')
AND u.username LIKE CONCAT('%', #{userName}, '%')
ORDER BY c.create_time DESC LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM `comment` c LEFT JOIN `user` u ON c.user_id = u.id LEFT JOIN `scenic_spot` s ON c.scenic_id = s.id
AND c.content LIKE CONCAT('%', #{content}, '%')
AND c.scenic_id = #{scenicId}
AND c.user_id = #{userId}
AND s.name LIKE CONCAT('%', #{scenicName}, '%')
AND u.username LIKE CONCAT('%', #{userName}, '%')
