mapper.select:
SELECT * FROM `order`
AND order_no LIKE CONCAT('%', #{orderNo}, '%')
AND visitor_name LIKE CONCAT('%', #{visitorName}, '%')
AND visitor_phone LIKE CONCAT('%', #{visitorPhone}, '%')
AND status = #{status}
ORDER BY create_time DESC LIMIT #{offset}, #{size}

SELECT * FROM `order`

    user_id = #{userId}

        AND status = #{status}


ORDER BY create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM `order`

    user_id = #{userId}

        AND status = #{status}
