mapper.select:
SELECT * FROM `ticket`


        AND ticket_name LIKE CONCAT('%', #{ticketName}, '%')


        AND ticket_type = #{ticketType}


        AND scenic_id = #{scenicId}


ORDER BY create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*) FROM `ticket`


        AND ticket_name LIKE CONCAT('%', #{ticketName}, '%')


        AND ticket_type = #{ticketType}


        AND scenic_id = #{scenicId}
